package com.syntax.class02;

public class AritmethicOperationTask1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double num1=12.99;
		double num2=10.99;
		
		double add=num1+num2;
		double sub=num1-num2;
		double mult=num1*num2;
		double div=num1/num2;
		System.out.println("The addition of 2 numbers "+num1+" "+num2+" is equal to "+add);
		System.out.println("The subtraction of 2 numbers "+num1+" "+num2+" is equal to "+sub);
		System.out.println("The ultipication of 2 numbers "+num1+" "+num2+" is equal to "+mult);
		System.out.println("The division of 2 numbers "+num1+" "+num2+" is equal to "+div);

	}

}
//Write a Java program to add, subtract, multiply and divide 2 decimal values. 
//Your program should say. “The _______ of 2 numbers ___ and ___ is equal to _____”